import{_ as l,c as i,o as e,a2 as t}from"./chunks/framework.C2eRlmqf.js";const s=JSON.parse('{"title":"前端架构与工程化：大纲","description":"","frontmatter":{"title":"前端架构与工程化：大纲"},"headers":[],"relativePath":"frontend-architecture/__outline.md","filePath":"frontend-architecture/__outline.md","lastUpdated":1769026429000}'),r={name:"frontend-architecture/__outline.md"};function o(h,a,n,u,d,_){return e(),i("div",null,[...a[0]||(a[0]=[t('<blockquote><p>提示：本大纲为 AI 生成草案，仅用于后续写作与总结时的提示与结构对齐，不代表正文已开始撰写。WIP 仅用于标记“正在撰写中”的内容。</p></blockquote><h2 id="_0-范围" tabindex="-1">0. 范围 <a class="header-anchor" href="#_0-范围" aria-label="Permalink to &quot;0. 范围&quot;">​</a></h2><ul><li>目标：把“架构能力”从概念落到可执行的决策与评审体系</li><li>关注：可维护性、可扩展性、稳定性、性能、协作效率、成本与风险</li><li>口径：不谈“架构很牛”，只谈“这个决策为什么值、哪里会翻车、怎么兜底”</li></ul><h2 id="_1-架构从哪来-先定义问题" tabindex="-1">1. 架构从哪来：先定义问题 <a class="header-anchor" href="#_1-架构从哪来-先定义问题" aria-label="Permalink to &quot;1. 架构从哪来：先定义问题&quot;">​</a></h2><h3 id="_1-1-需求口径-先把问题写清楚" tabindex="-1">1.1 需求口径：先把问题写清楚 <a class="header-anchor" href="#_1-1-需求口径-先把问题写清楚" aria-label="Permalink to &quot;1.1 需求口径：先把问题写清楚&quot;">​</a></h3><ul><li>目标：业务目标与用户价值（不是“要一个页面”）</li><li>约束：性能预算、兼容性、合规、交付周期</li><li>风险：最可能翻车的三件事与预案</li><li>指标：怎么衡量成功与失败</li><li>回滚：回滚粒度、回滚策略与演练</li></ul><h3 id="_1-2-分层与边界-复杂度要有收敛点" tabindex="-1">1.2 分层与边界：复杂度要有收敛点 <a class="header-anchor" href="#_1-2-分层与边界-复杂度要有收敛点" aria-label="Permalink to &quot;1.2 分层与边界：复杂度要有收敛点&quot;">​</a></h3><ul><li>领域模型：把业务事实表达成稳定的对象与约束</li><li>数据流：命令、事件与状态的关系</li><li>渲染层：组件只是表现，别把业务塞进组件里</li><li>基础设施：路由、请求、缓存、埋点、日志、权限</li></ul><h2 id="_2-状态管理与数据流" tabindex="-1">2. 状态管理与数据流 <a class="header-anchor" href="#_2-状态管理与数据流" aria-label="Permalink to &quot;2. 状态管理与数据流&quot;">​</a></h2><h3 id="_2-1-状态分类-先区分-事实来源" tabindex="-1">2.1 状态分类：先区分“事实来源” <a class="header-anchor" href="#_2-1-状态分类-先区分-事实来源" aria-label="Permalink to &quot;2.1 状态分类：先区分“事实来源”&quot;">​</a></h3><ul><li>UI 状态：短生命周期、可丢弃、可重算</li><li>领域状态：需要一致性与约束，适合集中管理</li><li>服务端状态：缓存、重试、并发与一致性策略</li></ul><h3 id="_2-2-数据流设计-能回放-才可治理" tabindex="-1">2.2 数据流设计：能回放，才可治理 <a class="header-anchor" href="#_2-2-数据流设计-能回放-才可治理" aria-label="Permalink to &quot;2.2 数据流设计：能回放，才可治理&quot;">​</a></h3><ul><li>命令：意图明确、可校验、可拒绝</li><li>事件：用于通知与观测，不要承载核心逻辑</li><li>回放：把线上问题变成“可复现”而不是“凭感觉”</li></ul><h2 id="_3-组件体系与设计系统" tabindex="-1">3. 组件体系与设计系统 <a class="header-anchor" href="#_3-组件体系与设计系统" aria-label="Permalink to &quot;3. 组件体系与设计系统&quot;">​</a></h2><h3 id="_3-1-复用策略-把-复用-从口号变成成本模型" tabindex="-1">3.1 复用策略：把“复用”从口号变成成本模型 <a class="header-anchor" href="#_3-1-复用策略-把-复用-从口号变成成本模型" aria-label="Permalink to &quot;3.1 复用策略：把“复用”从口号变成成本模型&quot;">​</a></h3><ul><li>工具库：稳定、可测试、低耦合，优先沉到这里</li><li>组件：承载交互与视觉，避免承载业务规则</li><li>hooks：抽离状态与副作用，但要警惕“隐式耦合”</li><li>模板化：对齐结构与约束，降低沟通成本</li></ul><h3 id="_3-2-设计系统-一致性来自约束" tabindex="-1">3.2 设计系统：一致性来自约束 <a class="header-anchor" href="#_3-2-设计系统-一致性来自约束" aria-label="Permalink to &quot;3.2 设计系统：一致性来自约束&quot;">​</a></h3><ul><li>视觉 Token：颜色、字号、间距、暗黑模式</li><li>交互规范：键盘操作、焦点管理、表单验证口径</li><li>a11y/i18n：从设计与组件契约阶段介入，而不是上线前补洞</li></ul><h2 id="_4-工程质量与稳定性" tabindex="-1">4. 工程质量与稳定性 <a class="header-anchor" href="#_4-工程质量与稳定性" aria-label="Permalink to &quot;4. 工程质量与稳定性&quot;">​</a></h2><h3 id="_4-1-测试-覆盖面与成本的平衡" tabindex="-1">4.1 测试：覆盖面与成本的平衡 <a class="header-anchor" href="#_4-1-测试-覆盖面与成本的平衡" aria-label="Permalink to &quot;4.1 测试：覆盖面与成本的平衡&quot;">​</a></h3><ul><li>单测：工具库与核心逻辑优先</li><li>集成：模块边界与关键流程</li><li>E2E：关键链路兜底，避免把测试写成另一个产品</li></ul><h3 id="_4-2-发布与灰度-把线上当成一个受控实验场" tabindex="-1">4.2 发布与灰度：把线上当成一个受控实验场 <a class="header-anchor" href="#_4-2-发布与灰度-把线上当成一个受控实验场" aria-label="Permalink to &quot;4.2 发布与灰度：把线上当成一个受控实验场&quot;">​</a></h3><ul><li>指标：上线前定义、上线后对齐</li><li>报警：触发条件与分级，避免报警疲劳</li><li>回滚：一键回滚、兼容旧数据与降级策略</li></ul><h2 id="_5-性能治理-工程化视角" tabindex="-1">5. 性能治理（工程化视角） <a class="header-anchor" href="#_5-性能治理-工程化视角" aria-label="Permalink to &quot;5. 性能治理（工程化视角）&quot;">​</a></h2><h3 id="_5-1-性能口径与预算-让-快-可衡量" tabindex="-1">5.1 性能口径与预算：让“快”可衡量 <a class="header-anchor" href="#_5-1-性能口径与预算-让-快-可衡量" aria-label="Permalink to &quot;5.1 性能口径与预算：让“快”可衡量&quot;">​</a></h3><ul><li>指标：加载、交互、滚动、内存、长任务</li><li>预算：每类页面/功能有不同预算，超预算必须有原因</li></ul><h3 id="_5-2-防回归-性能是一个系统-不是一次优化" tabindex="-1">5.2 防回归：性能是一个系统，不是一次优化 <a class="header-anchor" href="#_5-2-防回归-性能是一个系统-不是一次优化" aria-label="Permalink to &quot;5.2 防回归：性能是一个系统，不是一次优化&quot;">​</a></h3><ul><li>基准：代表性场景与固定设备口径</li><li>看板：趋势 &gt; 单点；对比 &gt; 绝对值</li><li>机制：变更影响评估、回归门禁与复盘闭环</li></ul><h2 id="_6-拟发布文章方向" tabindex="-1">6. 拟发布文章方向 <a class="header-anchor" href="#_6-拟发布文章方向" aria-label="Permalink to &quot;6. 拟发布文章方向&quot;">​</a></h2><ul><li>架构评审清单：目标/约束/风险/指标/回滚的模板化</li><li>状态与数据流：可回放系统的设计方法</li><li>工程稳定性：从报警到复盘的完整闭环</li><li>性能治理：预算化、门禁化与规模化落地</li></ul>',30)])])}const b=l(r,[["render",o]]);export{s as __pageData,b as default};
